CREATE TABLE PERSON (ID INTEGER AUTO_INCREMENT NOT NULL, DTYPE VARCHAR(31), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE MOVIE (ID INTEGER AUTO_INCREMENT NOT NULL, TITLE VARCHAR(255), LIBRARY_ID INTEGER, PRIMARY KEY (ID))
CREATE TABLE MOVIELIBRARY (ID INTEGER AUTO_INCREMENT NOT NULL, NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE ACTOR (ID INTEGER NOT NULL, OSCARNOMINATIONS INTEGER, PRIMARY KEY (ID))
CREATE TABLE DIRECTOR (ID INTEGER NOT NULL, OSCARWINS INTEGER, PRIMARY KEY (ID))
CREATE TABLE MOVIE2ACTOR (moviesActed_ID INTEGER NOT NULL, actors_ID INTEGER NOT NULL, PRIMARY KEY (moviesActed_ID, actors_ID))
CREATE TABLE MOVIE2DIRECTOR (moviesDirected_ID INTEGER NOT NULL, directors_ID INTEGER NOT NULL, PRIMARY KEY (moviesDirected_ID, directors_ID))
ALTER TABLE MOVIE ADD CONSTRAINT FK_MOVIE_LIBRARY_ID FOREIGN KEY (LIBRARY_ID) REFERENCES MOVIELIBRARY (ID)
ALTER TABLE ACTOR ADD CONSTRAINT FK_ACTOR_ID FOREIGN KEY (ID) REFERENCES PERSON (ID)
ALTER TABLE DIRECTOR ADD CONSTRAINT FK_DIRECTOR_ID FOREIGN KEY (ID) REFERENCES PERSON (ID)
ALTER TABLE MOVIE2ACTOR ADD CONSTRAINT FK_MOVIE2ACTOR_moviesActed_ID FOREIGN KEY (moviesActed_ID) REFERENCES MOVIE (ID)
ALTER TABLE MOVIE2ACTOR ADD CONSTRAINT FK_MOVIE2ACTOR_actors_ID FOREIGN KEY (actors_ID) REFERENCES PERSON (ID)
ALTER TABLE MOVIE2DIRECTOR ADD CONSTRAINT FK_MOVIE2DIRECTOR_moviesDirected_ID FOREIGN KEY (moviesDirected_ID) REFERENCES MOVIE (ID)
ALTER TABLE MOVIE2DIRECTOR ADD CONSTRAINT FK_MOVIE2DIRECTOR_directors_ID FOREIGN KEY (directors_ID) REFERENCES PERSON (ID)
